var AppEnvironment = require('../core/AppEnvironment');
var asyncRequire = require("noder-js/asyncRequire").create(module);

var settings = AppEnvironment.applicationSettings.contextualMenu;
var isContextualMenuEnabled = settings && settings.enabled;

var load = function () {};

/**
 * Load the contextual menu if needed
 * @return {Promise}
 */
load.$preload = function () {
    if (isContextualMenuEnabled) {
        return asyncRequire("../tools/contextual/ContextualMenu");
    }
};

module.exports = {
    load : load
};